'use strict';

const express = require('express');
const app = express();

const bodyParser = require('body-parser');
const jsonParser = bodyParser.json();

module.exports = (routes) => {
    routes.map((route) => {
        switch (route.method) {
            case 'POST':
                app.post(route.link, jsonParser, route.callback);
                return;
            case 'PATCH':
                app.patch(route.link, jsonParser, route.callback);
                return;
            case 'PUT':
                app.put(route.link, jsonParser, route.callback);
                return;
            case 'DELETE':
                app.delete(route.link, route.callback);
                return;
            default:
                app.get(route.link, route.callback);
        }
    })

    return app;
};

'use strict';

module.exports = (db) => {
    return [
        {
            link: '/health',
            method: 'GET',
            tags: ['Health'],
            description: 'Checks system status',
            responses: {
                200: {
                    description: 'OK'
                },
            },
            callback: (_, res) => res.send('Healthy')
        },
        {
            link: '/rides',
            method: 'POST',
            tags: ['Rides'],
            description: 'Create new ride in the system',
            parameters: [
                {
                    name: 'body',
                    in: 'body'
                },
            ],
            responses: {
                200: {
                    description: 'New ride is created'
                },
            },
            callback: (req, res) => {
                const startLatitude = Number(req.body.start_lat);
                const startLongitude = Number(req.body.start_long);
                const endLatitude = Number(req.body.end_lat);
                const endLongitude = Number(req.body.end_long);
                const riderName = req.body.rider_name;
                const driverName = req.body.driver_name;
                const driverVehicle = req.body.driver_vehicle;

                if (startLatitude < -90 || startLatitude > 90 || startLongitude < -180 || startLongitude > 180) {
                    return res.send({
                        error_code: 'VALIDATION_ERROR',
                        message: 'Start latitude and longitude must be between -90 - 90 and -180 to 180 degrees respectively'
                    });
                }

                if (endLatitude < -90 || endLatitude > 90 || endLongitude < -180 || endLongitude > 180) {
                    return res.send({
                        error_code: 'VALIDATION_ERROR',
                        message: 'End latitude and longitude must be between -90 - 90 and -180 to 180 degrees respectively'
                    });
                }

                if (typeof riderName !== 'string' || riderName.length < 1) {
                    return res.send({
                        error_code: 'VALIDATION_ERROR',
                        message: 'Rider name must be a non empty string'
                    });
                }

                if (typeof driverName !== 'string' || driverName.length < 1) {
                    return res.send({
                        error_code: 'VALIDATION_ERROR',
                        message: 'Rider name must be a non empty string'
                    });
                }

                if (typeof driverVehicle !== 'string' || driverVehicle.length < 1) {
                    return res.send({
                        error_code: 'VALIDATION_ERROR',
                        message: 'Rider name must be a non empty string'
                    });
                }

                var values = [req.body.start_lat, req.body.start_long, req.body.end_lat, req.body.end_long, req.body.rider_name, req.body.driver_name, req.body.driver_vehicle];

                const result = db.run('INSERT INTO Rides(startLat, startLong, endLat, endLong, riderName, driverName, driverVehicle) VALUES (?, ?, ?, ?, ?, ?, ?)', values, function (err) {
                    if (err) {
                        return res.send({
                            error_code: 'SERVER_ERROR',
                            message: 'Unknown error'
                        });
                    }

                    db.all('SELECT * FROM Rides WHERE rideID = ?', this.lastID, function (err, rows) {
                        if (err) {
                            return res.send({
                                error_code: 'SERVER_ERROR',
                                message: 'Unknown error'
                            });
                        }

                        res.send(rows);
                    });
                });
            }
        },
        {
            link: '/rides',
            method: 'GET',
            tags: ['Rides'],
            description: 'Get all rides from the system',
            responses: {
                200: {
                    description: 'OK'
                },
            },
            callback: (_, res) => {
                db.all('SELECT * FROM Rides', function (err, rows) {
                    if (err) {
                        return res.send({
                            error_code: 'SERVER_ERROR',
                            message: 'Unknown error'
                        });
                    }

                    if (rows.length === 0) {
                        return res.send({
                            error_code: 'RIDES_NOT_FOUND_ERROR',
                            message: 'Could not find any rides'
                        });
                    }

                    res.send(rows);
                });
            }
        },
        {
            link: '/rides/:id',
            method: 'GET',
            tags: ['Rides'],
            description: 'Get specific ride from the system',
            parameters: [
                {
                    name: 'id',
                    schema: {
                        type: 'integer'
                    },
                    in: 'path'
                },
            ],
            responses: {
                200: {
                    description: 'OK'
                },
            },
            callback: (req, res) => {
                db.all(`SELECT * FROM Rides WHERE rideID='${req.params.id}'`, function (err, rows) {
                    if (err) {
                        return res.send({
                            error_code: 'SERVER_ERROR',
                            message: 'Unknown error'
                        });
                    }

                    if (rows.length === 0) {
                        return res.send({
                            error_code: 'RIDES_NOT_FOUND_ERROR',
                            message: 'Could not find any rides'
                        });
                    }

                    res.send(rows);
                });
            }
        },
    ];
}

'use strict';

module.exports = (db) => {
    const createRideTableSchema = `
        CREATE TABLE Rides
        (
        rideID INTEGER PRIMARY KEY AUTOINCREMENT,
        startLat DECIMAL NOT NULL,
        startLong DECIMAL NOT NULL,
        endLat DECIMAL NOT NULL,
        endLong DECIMAL NOT NULL,
        riderName TEXT NOT NULL,
        driverName TEXT NOT NULL,
        driverVehicle TEXT NOT NULL,
        created DATETIME default CURRENT_TIMESTAMP
        )
    `;

    db.run(createRideTableSchema);

    return db;
};

'use strict';

const fs = require('fs');
const pjson = require('../package.json');

module.exports = (port, routes) => {
    let data = {
        swagger: '2.0',
        info: {
            version: pjson.version,
            title: pjson.name,
            description: pjson.description,
        },
        host: `localhost:${port}`,
        basePath: '',
        tags: [],
        schemes: ['http'],
        consumes: ['application/json'],
        produces: ['application/json'],
        paths: {}
    }

    routes.map((route) => {
        if (data.paths[route.link] == null) {
            data.paths[route.link] = {};
        }

        data.paths[route.link][route.method.toLowerCase()] = {
            tags: route.tags,
            summary: route.description,
            responses: route.responses,
            requestBody: route.requestBody,
            parameters: route.parameters
        }
    })

    fs.writeFileSync('swagger.json', JSON.stringify(data), 'utf8', (err) => {
        if (err) {
            console.log(err);
            throw err;
        };
    });
}

